		<script>
			setTimeout(function () {
	        	verifExpand();
	    	}, 10);
	    	$("#newEmInp").on("keyup", function (e) {
			    if (e.key === "Enter" || e.keyCode === 13) {
			        $("#newEmInp").blur();
			        changeunverifEmail();
			    }
			});
			function changeunverifEmail(){
				var newEmail = $("#newEmInp").val();
				var pwd = $("#pwdinp").val();
				var username = $("#uninp").val();
				$.ajax({
					url:"controllers/changeEmailController.php",
					method:"POST",
					data:{
						"email": newEmail,
						"username": username,
						"password": pwd
					},
					dataType:"JSON",
					beforeSend: function(){
						try{
							autoHideErrorTimeout.clearTimeout();
						}catch{}
							displayLoadAlert("Changing email");
					},
					success: function(response){
						displayError(response);
						if(response[0]==0){
							//If change was succesful do this
							verifRetract();
							setTimeout(() => { reloadPopup() }, 1000);
							function reloadPopup(){
								$(".unverified").remove();
								sendLogin(); }
						}
					},
				});
			}
			$("#resendVer").click(function(){
				$.ajax({
					url:"controllers/emailController.php",
					method:"POST",
					data:{
						"action": "sendVerificationEmail",
						"email": "'.$message.'"
					},
					dataType:"JSON",
					beforeSend: function(){
						try{
							autoHideErrorTimeout.clearTimeout();
						}catch{}
							displayLoadAlert("Sending email");
					},
					success: function(response){
						displayError(response);
					},
				});
			});
			$("#checkVer").click(function(){
				var autoHideErrorTimeout;
				$.ajax({
					url:"controllers/loginController.php",
					method:"POST",
					data:{
						username:$("#uninp").val(),
						password:$("#pwdinp").val()
					},
					dataType:"JSON",
					beforeSend: function(){
						try{
							autoHideErrorTimeout.clearTimeout();
						}catch{}
						displayLoadAlert("Attempting to log in");
					},
					success: function(data){
						var successCode = data[0];
						if (successCode == 2) { //verif needed
							displayError(data.splice(2,4));
						} else if(successCode == 0){ //log in
							displayError(data);
							setTimeout(() => { reloadPage() }, 500); //change this value for wait before reloading in ms
							function reloadPage(){
								location.reload();
							}
						}else{//Error
							displayError(data);
						}
					}
				});
			});
		</script>
';

return $alert;
?>
